mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT c.catenum, c.created, boardNum, subject, hitCount, boardName
        FROM COMMAINBOARD b
        RIGHT OUTER JOIN commaincate c ON b.cateNum=c.cateNum
        WHERE c.catenum=#{cateNum}
        ORDER BY boardNum DESC
	) tb WHERE ROWNUM <=5
) WHERE rnum >=1
