mapper.select:
	 SELECT * FROM (
    	SELECT ROWNUM rnum, tb.* FROM (
       	 	SELECT boardnum,subject,content,created,hitCount,cateNum,c.memberNum
       	 	FROM commainboard c
	       	JOIN member2 m ON m.membernum=c.membernum
	        WHERE cateNum=#{cateNum}
	        ORDER BY boardnum DESC

    	) tb WHERE ROWNUM <= #{end}
	) WHERE rnum >= #{start}
