mapper.insert:
INSERT INTO communitynotice (noticenum, subject, content, savefilename, originalfilename)
VALUES (seq_communitynotice.NEXTVAL,#{subject},#{content},#{saveFilename, jdbcType=VARCHAR}
		,#{originalFilename, jdbcType=VARCHAR})

mapper.select:
	SELECT * FROM(
		SELECT ROWNUM rnum, tb.* FROM(
			SELECT noticenum, subject, hitCount, TO_CHAR(created, 'yyyy-mm-dd') as created
			FROM communitymainnotice
			
				
					
				
			
			ORDER BY noticenum DESC
	
		) tb WHERE ROWNUM <= #{end}
	) WHERE rnum >= #{start}
