SELECT num, memberNum, sendDate, readDate, deleteStatus, content
FROM messenger
WHERE num=#{num}

SELECT num, friendNum, sendDate, content, proFileSaveFileName FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		
		
	) tb WHERE ROWNUM<=#{end}
) m
LEFT OUTER JOIN mygram g ON m.friendNum=g.memberNum
WHERE rnum>=#{start}

ORDER BY sendDate ASC
