mapper.select:
SELECT friendNum FROM gramfriend
WHERE memberNum=#{memberNum} AND friendNum NOT IN (
	SELECT memberNum FROM gramfriend
	WHERE friendNum=#{memberNum}
)

SELECT NVL(COUNT(*), 0) FROM gramfriend
WHERE memberNum=#{memberNum} AND friendNum NOT IN (
	SELECT memberNum FROM gramfriend
	WHERE friendNum=#{memberNum}
)
