mapper.select:
SELECT * FROM(
	SELECT ROWNUM rnum, tb. * FROM (
		SELECT num, m2.name, g.memberNum, blogNum, content, created
		FROM gramtimeline g
		JOIN member2 m2 ON g.memberNum=m2.memberNum
		WHERE g.blogNum=#{blogNum}
		ORDER BY num DESC

	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
