mapper.select:
SELECT * FROM(
	SELECT ROWNUM rnum, tb. * FROM (
		SELECT g.num, m2.name, g.memberNum, g.saveFilename, content, created, nvl(replyCount, 0) replyCount
		FROM gramtimeline g
		JOIN member2 m2 ON g.memberNum=m2.memberNum
		LEFT OUTER JOIN 
			(
			      SELECT num, NVL(COUNT(*), 0) replyCount FROM gramreply
	              WHERE answer=0
			      GROUP BY num
			) rr ON g.num = rr.num
		ORDER BY num DESC

	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
