sql.if:
(	(startdate BETWEEN TO_DATE(#{startDate},'YYYY/MM/DD') AND  TO_DATE(#{endDate},'YYYY/MM/DD'))  
OR  (enddate  BETWEEN TO_DATE(#{startDate},'YYYY/MM/DD')  AND  TO_DATE(#{endDate},'YYYY/MM/DD'))
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM( SELECT r1.reservnum,membernum,NVL(signspot,0) signspot,rmresnum,TO_CHAR(startdate,'YYYY/MM/DD') startDate, TO_CHAR(enddate,'YYYY/MM/DD')endDate,userName, r2.roomcode,roomname,ro.floornum, floorname, f.spotcode, spotname FROM reserv r1 JOIN rmres r2 ON r1.reservnum=r2.reservnum JOIN room ro ON ro.roomcode=r2.roomcode JOIN floor f ON f.floornum=ro.floornum JOIN spot s ON s.spotcode=f.spotcode
)tb
WHERE ROWNUM <= #{end} )WHERE rnum >= #{start}
