mapper.select:
SELECT NVL(COUNT(*),0) FROM sc
	
		
		  
		  
			AND qnaCode=#{searchCode}
		  
		
		
		  
			 qnaCode=#{searchCode}

		SELECT * FROM(
			SELECT ROWNUM rnum, tb.* FROM(
				SELECT num,content,subject,created,answer,sc.qnacode, qnaname ,m2.membernum,
				        name,email
				FROM sc
				JOIN member2 m2 ON sc.memberNum=m2.memberNum
				JOIN qnacate q ON sc.qnacode=q.qnacode
				
				
				  
				  
					AND sc.qnaCode=#{searchCode}
				  
				
				
				  
					 sc.qnaCode=#{searchCode}
				  
							 
			 
				ORDER BY num DESC
			)tb
			
			WHERE ROWNUM <= #{end}
		)WHERE rnum >= #{start}

SELECT ROWNUM, tb.* FROM(
	SELECT num, subject
	FROM sc
	
		
		  
		  
			AND qnaCode=#{searchCode}
		  
		
		
		  
			 qnaCode=#{searchCode}
		  
			
		AND	num > #{num}		 
	 
	ORDER BY num
	)tb
WHERE ROWNUM=1

SELECT ROWNUM, tb.* FROM(
	SELECT num, subject
	FROM sc
	
		
		  
		  
			AND qnaCode=#{searchCode}
		  
		
		
		  
			 qnaCode=#{searchCode}
		  
			
		AND	num < #{num}		 
	 
	ORDER BY num DESC
	)tb
WHERE ROWNUM=1
