mapper.select:
	SELECT * FROM(
	    SELECT ROWNUM rnum, tb.* FROM(
	        SELECT p.placecode, placement, p.floornum, created,floorName,s.spotcode,spotname,region
	        FROM placement p
	        JOIN floor f ON p.floornum=f.floornum
	        JOIN spot s ON f.spotcode=s.spotcode
	        
	        	
	        		
	        	
	        
	        ORDER BY p.placecode desc
	    )tb

	    WHERE ROWNUM <=#{end}
	)WHERE rnum >= #{start}
