mapper.select:
		SELECT * FROM(
		    SELECT ROWNUM rnum, tb.*FROM(
		        SELECT  boardNum, content, TO_CHAR(f.created,'YYYY-MM-DD') created, hitcount, writer, f.cmoimcode,m.name
		            FROM freeboard f 
		            JOIN commoim c ON f.cmoimcode = c.cmoimcode
		            JOIN member2 m ON f.writer = m.membernum
		            WHERE f.cmoimCode = #{cmoimCode}
		            ORDER BY boardNum DESC
	
		    ) tb WHERE ROWNUM <= #{end}
		)WHERE rnum >= #{start}
